$(document).ready(function(){$("#myNavbar a").on('click',scrollSmooth);$("a.btn").on('click',scrollSmooth)});function scrollSmooth(event){if(this.hash!==""){event.preventDefault();var hash=this.hash;$('html, body').animate({scrollTop:$(hash).offset().top-91},800)}}
